// Get topic from "Function - Build Writer Prompt"
const topic =
  $node["Function - Build Writer Prompt"]?.json?.topic || "";

// Get draft text from Gemini Writer response
const draft =
  $json.candidates?.[0]?.content?.parts?.[0]?.text?.trim() || "";

// Return for next nodes
return [
  {
    json: {
      topic,
      draft,
    },
  },
];

const topic = $json["topic"];
const draft = $json["draft"];

const prompt = `You are a strict editor. You hate generic buzzwords.

You will receive a LinkedIn post draft.
Your tasks:
1) Briefly critique the draft (maximum 3 sentences).
2) Rewrite it to be sharper, more concrete, and more impactful.

RESPONSE FORMAT (IMPORTANT):
- Respond ONLY in valid JSON.
- No additional text, no markdown, no explanations.
- Use exactly these keys: "critique" and "final_post".

Example:
{
  "critique": "The draft is too vague and uses generic language...",
  "final_post": "Here is the revised, punchier version..."
}

DRAFT:
"""${draft}"""`;

return [{ json: { topic, draft, prompt, writerUsage: $json.writerUsage } }];

const topic = $item(0).$node["Function - Build Editor Prompt"].json.topic;
const draft = $item(0).$node["Function - Build Editor Prompt"].json.draft;
const writerUsage = $item(0).$node["Function - Build Editor Prompt"].json.writerUsage || {};

const res = $json;
let rawText = "";

try {
  const candidates = res.candidates || [];
  if (candidates.length > 0 && candidates[0].content && candidates[0].content.parts && candidates[0].content.parts[0].text) {
    rawText = candidates[0].content.parts[0].text.trim();
  }
} catch (e) {
  rawText = "[ERROR] Could not parse editor response";
}

// Clean markdown fences
let cleaned = rawText.trim();
if (cleaned.startsWith("```")) {
  const firstNewline = cleaned.indexOf("\n");
  if (firstNewline !== -1) cleaned = cleaned.slice(firstNewline + 1);
  if (cleaned.endsWith("```")) cleaned = cleaned.slice(0, -3);
}

let critique = "";
let finalPost = draft;

try {
  const parsed = JSON.parse(cleaned);
  critique = (parsed.critique || "").trim();
  finalPost = (parsed.final_post || draft).trim();
} catch (e) {
  critique = `Failed to parse JSON from editor. Raw: ${cleaned}`;
}

const editorUsage = res.usageMetadata || {};

return [{
  json: {
    topic,
    draft,
    final_post: finalPost,
    critique,
    writerUsage,
    editorUsage,
    rawEditorOutput: rawText
  }
}];

// Get main fields from previous node
const topic      = $json.topic      || "";
const draft      = $json.draft      || "";
const final_post = $json.final_post || "";

// usage objects added from Parse Editor JSON
const w = $json.writerUsage || {};
const e = $json.editorUsage || {};

// INPUT TOKENS — always promptTokenCount
const writer_input_tokens  = w.promptTokenCount || 0;
const editor_input_tokens  = e.promptTokenCount || 0;

// OUTPUT TOKENS — take totalTokenCount - promptTokenCount
// because candidatesTokenCount is NOT always present in Gemini API responses
const writer_output_tokens = (w.totalTokenCount || 0) - (w.promptTokenCount || 0);
const editor_output_tokens = (e.totalTokenCount || 0) - (e.promptTokenCount || 0);

// totals
const input_tokens  = writer_input_tokens + editor_input_tokens;
const output_tokens = writer_output_tokens + editor_output_tokens;
const total_tokens  = input_tokens + output_tokens;

// Prices per 1000 tokens (example)
const WRITER_IN_PRICE  = 0.00035;
const WRITER_OUT_PRICE = 0.00070;
const EDITOR_IN_PRICE  = 0.00350;
const EDITOR_OUT_PRICE = 0.01050;

// Cost calculation
const writer_cost =
  (writer_input_tokens  / 1000) * WRITER_IN_PRICE +
  (writer_output_tokens / 1000) * WRITER_OUT_PRICE;

const editor_cost =
  (editor_input_tokens  / 1000) * EDITOR_IN_PRICE +
  (editor_output_tokens / 1000) * EDITOR_OUT_PRICE;

const estimated_cost = writer_cost + editor_cost;

// Return results
return [
  {
    json: {
      topic,
      draft,
      final_post,

      writer_input_tokens,
      writer_output_tokens,

      editor_input_tokens,
      editor_output_tokens,

      input_tokens,
      output_tokens,
      total_tokens,
      estimated_cost,
    },
  },
];
